mapper.select:
SELECT bookmark, pencil AS dailyParticipate
FROM user
WHERE id = #{userId}

SELECT COUNT(distinct user_id) AS cnt
FROM daily_event d, book b, feed f, book_keyword bk
WHERE d.id = #{dailyId} AND bk.book_id = b.id AND b.id = f.book_id AND bk.keyword_id = d.keyword_id AND DATE(f.create_at) = DATE(d.create_at);
